import React, { Component } from 'react'
import {connect} from 'react-redux'
// import PostForm from './PostForm'
import { confirmAlert } from 'react-confirm-alert'; 
import 'bootstrap/dist/css/bootstrap.min.css'
import Coeur from './coeur'
import 'react-confirm-alert/src/react-confirm-alert.css'

class Post extends Component {

  update = () => (
		confirmAlert({
			customUI: ({ onClose }) => {

				return (
					<div className="custom-ui" >
					<form className={'edit'}>
						<input type='text' className={'input'} ref={(input)=>this.newPrenom = input }defaultValue={this.props.post.prenom}  />	
            <button className={'boutton3 nom'}>
							comment
						</button>
						</form>
					</div>
				);
			}
		})
	)

  render() {
  return (
    // <div key = {this.props.post.nb} className="col-md-4">
    //<div className={'container'}>
    <div className={'row'}>
      <div key = {this.props.post.nb} className={'col-md-4'} >
        <div>
          <b>{this.props.post.produit}</b>
        </div>
        <div className = {'block'}>
          {this.props.post.description }
        </div>
        <div className={'prix'}>
          {this.props.post.prix }
        </div>         

          <hr/>
        <table>
            <tr>
                <td><Coeur/></td>
                <td onClick={()=>this.update()}>Comment</td>
            </tr>
        </table>    
      </div>
      </div>
    //</div>




  );
 }
}

export default connect()(Post);
